			dp[i] += strings.Count(string(S[min(2*j-i+1, N):]), t(string(S[i]), string(S[j])))
			dp[i] += strings.Count(string(S[j+1:min(2*j-i, N)]), t(string(S[i]), string(S[j])))

			// for k := j + 1; k < N; k++ {
